function [fitresult, gof] = createFit(V, I, w)
%CREATEFIT(V,I,W)
%  Create a fit.
%
%  Data for 'untitled fit 1' fit:
%      X Input: V
%      Y Output: I
%      Weights: w
%  Output:
%      fitresult : a fit object representing the fit.
%      gof : structure with goodness-of fit info.
%
%  See also FIT, CFIT, SFIT.

%  Auto-generated by MATLAB on 27-Nov-2022 23:11:17


%% Fit: 'untitled fit 1'.
[xData, yData, weights] = prepareCurveData( V, I, w );

% Set up fittype and options.
ft = fittype( 'poly1' );
excludedPoints = xData < 0.7;
opts = fitoptions( 'Method', 'LinearLeastSquares' );
opts.Weights = weights;
opts.Exclude = excludedPoints;

% Fit model to data.
[fitresult, gof] = fit( xData, yData, ft, opts );

% Create a figure for the fit.
figure( 'Name', 'Fit' );

% Plot fit with data.
h = plot( fitresult, xData, yData, excludedPoints );
hold on
err = (1./w).^2;
errorbar(V, I, err, "LineStyle", "none", "Color", "black");
hold off
legend( h, 'Data', 'Excluded data', 'Fit', 'Location', 'NorthWest', 'Interpreter', 'none' );
% Label axes
title('I vs. V (Polarizzazione diretta)');
xlabel( 'V', 'Interpreter', 'none' );
ylabel( 'I', 'Interpreter', 'none' );
grid on

% Create a figure for the residuals.
figure( 'Name', 'Residuals' );

% Plot residuals.
h = plot( fitresult, xData, yData, excludedPoints, 'residuals' );
legend( h, 'Residuals', 'Excluded residuals', 'Zero Line', 'Location', 'NorthEast', 'Interpreter', 'none' );
% Label axes
title('Residuals');
xlabel( 'V', 'Interpreter', 'none' );
ylabel( 'I', 'Interpreter', 'none' );
grid on

disp('Goodness of Fit')
disp(gof)

disp('Threshold Voltage')
x = fzero(fitresult,0)

end


